import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IMinimalTodoTaskUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IMinimalTodoTaskUser";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IMinimalTodoTask } from "@ORGANIZATION/PROJECT-api/lib/structures/IMinimalTodoTask";

/**
 * Test attempting to delete a task belonging to another user to verify
 * proper authorization enforcement.
 *
 * This test validates that the system properly enforces authorization rules
 * by preventing users from accessing or modifying tasks that belong to
 * other users. The test follows a complete user journey:
 *
 * 1. Create the first user (user1) with taskUser role
 * 2. Create a task using user1's authentication context
 * 3. Create the second user (user2) with taskUser role
 * 4. Attempt to delete user1's task using user2's authentication context
 * 5. Verify the system blocks this unauthorized access with appropriate error
 *
 * Security Note: This test verifies a critical security boundary that
 * prevents data leakage and maintains user data isolation, which is
 * essential for multi-tenant applications.
 */
export async function test_api_task_delete_unauthorized_access(
  connection: api.IConnection,
) {
  // Create first user (user1) with taskUser role
  // Using a securely formatted email and password meeting complexity requirements
  const userEmail1: string = typia.random<string & tags.Format<"email">>();
  const userPassword1: string = "SecurePass123!"; // Meets requirements: 8+ chars, letters, numbers, special char

  const user1: IMinimalTodoTaskUser.IAuthorized =
    await api.functional.auth.taskUser.join(connection, {
      body: {
        email: userEmail1,
        password: userPassword1,
      } satisfies IMinimalTodoTaskUser.IJoin,
    });
  typia.assert(user1);

  // Create a task with user1's context
  // Using properly formatted paragraph with object parameters
  const task: IMinimalTodoTask =
    await api.functional.minimalTodo.taskUser.tasks.create(connection, {
      body: {
        title: RandomGenerator.paragraph({
          sentences: 3,
          wordMin: 5,
          wordMax: 10,
        }),
      } satisfies IMinimalTodoTask.ICreate,
    });
  typia.assert(task);

  // Verify the task was created successfully and has a valid ID
  TestValidator.predicate(
    "created task should have valid ID",
    task.id !== null && task.id !== undefined && task.id.length > 0,
  );

  // Create second user (user2) with taskUser role to switch authentication context
  const userEmail2: string = typia.random<string & tags.Format<"email">>();
  const userPassword2: string = "SecurePass456!"; // Different secure password for second user

  const user2: IMinimalTodoTaskUser.IAuthorized =
    await api.functional.auth.taskUser.join(connection, {
      body: {
        email: userEmail2,
        password: userPassword2,
      } satisfies IMinimalTodoTaskUser.IJoin,
    });
  typia.assert(user2);

  // Verify that we have two distinct users
  TestValidator.notEquals(
    "user1 and user2 should be different accounts",
    user1.user.id,
    user2.user.id,
  );

  // Attempt to delete user1's task with user2's authentication context
  // This should fail due to authorization rules preventing access to other users' tasks
  // Expecting either 403 Forbidden (access denied) or 404 Not Found (to prevent user enumeration)
  await TestValidator.error(
    "user2 should not be able to delete user1\'s task - access should be denied",
    async () => {
      await api.functional.minimalTodo.taskUser.tasks.erase(connection, {
        taskId: task.id,
      });
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IMinimalTodoTaskUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IMinimalTodoTaskUser";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IMinimalTodoTask } from "@ORGANIZATION/PROJECT-api/lib/structures/IMinimalTodoTask";

export async function test_api_task_delete_cascade_relationships(
  connection: api.IConnection,
) {
  // 1. Create taskUser account to establish authentication context
  const joinData = {
    email: typia.random<string & tags.Format<"email">>(),
    password: "password123",
  } satisfies IMinimalTodoTaskUser.IJoin;

  // Register the taskUser
  const auth = await api.functional.auth.taskUser.join(connection, {
    body: joinData,
  });
  typia.assert(auth);

  // 2. Create a new task that may have associated dependent records
  const createData = {
    title: RandomGenerator.paragraph({ sentences: 3 }),
  } satisfies IMinimalTodoTask.ICreate;

  const task = await api.functional.minimalTodo.taskUser.tasks.create(
    connection,
    {
      body: createData,
    },
  );
  typia.assert(task);

  // 3. Delete the task - this should trigger CASCADE deletion of related records
  // Verify the delete operation succeeds (204 No Content expected)
  await api.functional.minimalTodo.taskUser.tasks.erase(connection, {
    taskId: task.id,
  });

  // 4. Verify cascade deletion behavior by attempting to create a new task
  // This ensures the system state remains consistent after cascade deletion
  // If cascade deletion failed and left orphaned records, this operation might fail
  const newTaskData = {
    title: RandomGenerator.paragraph({ sentences: 3 }),
  } satisfies IMinimalTodoTask.ICreate;

  const newTask = await api.functional.minimalTodo.taskUser.tasks.create(
    connection,
    {
      body: newTaskData,
    },
  );
  typia.assert(newTask);
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IMinimalTodoTaskUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IMinimalTodoTaskUser";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";

export async function test_api_task_user_registration_success(
  connection: api.IConnection,
): Promise<void> {
  // Generate test data
  // Create password with minimum 8 characters containing both letters and numbers
  const email: string = typia.random<string & tags.Format<"email">>();
  const password: string =
    RandomGenerator.alphabets(4) + RandomGenerator.alphaNumeric(4);

  // Create a new user account through the join process
  const authorized = await api.functional.auth.taskUser.join(connection, {
    body: {
      email,
      password,
    } satisfies IMinimalTodoTaskUser.IJoin,
  });
  typia.assert(authorized);

  // Validate response data
  TestValidator.equals(
    "user ID should be valid",
    authorized.user.id.length,
    36,
  ); // UUID length
  TestValidator.equals("user email matches", authorized.user.email, email);

  // Validate timestamps are in correct format and timezone (Asia/Seoul, UTC+9)
  await TestValidator.predicate(
    "created_at should be valid ISO datetime in Asia/Seoul timezone",
    async () => {
      const date = new Date(authorized.user.created_at);
      const timeDiff = date.getTime() + 9 * 60 * 60 * 1000; // Add 9 hours for UTC+9
      const koreaTime = new Date(timeDiff);
      return !isNaN(date.getTime()) && koreaTime.getTimezoneOffset() === -540; // Korea time offset is -540 minutes
    },
  );

  await TestValidator.predicate(
    "updated_at should be valid ISO datetime in Asia/Seoul timezone",
    async () => {
      const date = new Date(authorized.user.updated_at);
      const timeDiff = date.getTime() + 9 * 60 * 60 * 1000; // Add 9 hours for UTC+9
      const koreaTime = new Date(timeDiff);
      return !isNaN(date.getTime()) && koreaTime.getTimezoneOffset() === -540; // Korea time offset is -540 minutes
    },
  );

  // Verify authentication tokens are present and properly formatted
  TestValidator.equals(
    "access token exists and has content",
    authorized.token.access.length > 0,
    true,
  );
  TestValidator.equals(
    "refresh token exists and has content",
    authorized.token.refresh.length > 0,
    true,
  );
  TestValidator.predicate(
    "access token format is correct",
    () =>
      typeof authorized.token.access === "string" &&
      (authorized.token.access.startsWith("Bearer ") ||
        authorized.token.access.length > 10), // JWT tokens are typically long strings
  );

  // Verify tokens have expiration dates in the future
  await TestValidator.predicate(
    "access token has valid future expiration",
    async () => {
      const date = new Date(authorized.token.expired_at);
      return !isNaN(date.getTime()) && date > new Date();
    },
  );

  await TestValidator.predicate(
    "refresh token has valid future expiration",
    async () => {
      const date = new Date(authorized.token.refreshable_until);
      return !isNaN(date.getTime()) && date > new Date();
    },
  );

  // Verify deleted_at is null for active account
  TestValidator.equals(
    "deleted_at is null for active account",
    authorized.user.deleted_at,
    null,
  );

  // Verify user is automatically authenticated after registration
  // Check that connection has Authorization header
  TestValidator.predicate("connection has authorization header", () => {
    // Check if headers exist and Authorization is not null or undefined
    if (connection.headers === null || connection.headers === undefined)
      return false;

    const authHeader = connection.headers.Authorization;
    if (authHeader === null || authHeader === undefined) return false;

    // Verify Authorization header exists and is a string that starts with Bearer
    return typeof authHeader === "string" && authHeader.startsWith("Bearer ");
  });
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IMinimalTodoTaskUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IMinimalTodoTaskUser";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";

/**
 * Test that user registration fails when attempting to use an already
 * registered email address.
 *
 * This test validates the critical business rule that email addresses must
 * be unique across all user accounts in the system. It ensures data
 * integrity by preventing duplicate accounts and potential security issues
 * that could arise from multiple accounts sharing the same email.
 *
 * The test follows a complete workflow:
 *
 * 1. Creates a user account with a randomly generated email address
 * 2. Attempts to create a second account with the same email address
 * 3. Verifies that the system properly rejects the duplicate registration
 *
 * This prevents scenarios where users could circumvent account limits or
 * where customer support might accidentally create duplicate accounts. The
 * unique email constraint is enforced at the database level in the
 * minimal_todo_taskusers table, and this test ensures that constraint is
 * properly exposed through the API with appropriate error responses.
 */
export async function test_api_task_user_registration_duplicate_email_error(
  connection: api.IConnection,
) {
  // Generate a random email address to use for testing
  const email: string = typia.random<string & tags.Format<"email">>();

  // First, create a user with the generated email address
  // This establishes the email in the system to test the duplicate scenario
  const firstUser: IMinimalTodoTaskUser.IAuthorized =
    await api.functional.auth.taskUser.join(connection, {
      body: {
        email,
        password: "test1234",
      } satisfies IMinimalTodoTaskUser.IJoin,
    });
  typia.assert(firstUser);

  // Then, attempt to create a second user with the same email address
  // This should fail with an appropriate error since email addresses must be unique
  await TestValidator.error(
    "User registration should fail when attempting to use an already registered email address",
    async () => {
      await api.functional.auth.taskUser.join(connection, {
        body: {
          email,
          password: "test5678",
        } satisfies IMinimalTodoTaskUser.IJoin,
      });
    },
  );
}

