import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { IMinimalTodoTaskUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IMinimalTodoTaskUser";
import { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import { TaskuserPayload } from "../decorators/payload/TaskuserPayload";

export async function post__auth_taskUser_join(props: {
  body: {
    email: string & tags.Format<"email">;
    password: string;
  };
}): Promise<{
  user: {
    id: string & tags.Format<"uuid">;
    email: string & tags.Format<"email">;
    created_at: string & tags.Format<"date-time">;
    updated_at: string & tags.Format<"date-time">;
    deleted_at?: (string & tags.Format<"date-time">) | null;
  };
  token: {
    access: string;
    refresh: string;
    expired_at: string & tags.Format<"date-time">;
    refreshable_until: string & tags.Format<"date-time">;
  };
}> {
  const { email, password } = props.body;

  // Validate password complexity: minimum 8 characters with letters and numbers
  const hasLetters = /[a-zA-Z]/.test(password);
  const hasNumbers = /[0-9]/.test(password);
  if (password.length < 8 || !hasLetters || !hasNumbers) {
    throw new Error(
      "Password must be at least 8 characters long and contain both letters and numbers",
    );
  }

  // Check if user with this email already exists
  const existingUser = await MyGlobal.prisma.minimal_todo_taskusers.findFirst({
    where: { email },
  });

  if (existingUser) {
    throw new Error("Email already exists");
  }

  // Generate ID and hash password
  const id = v4() as string & tags.Format<"uuid">;
  const now = toISOStringSafe(new Date());

  const hashedPassword = await MyGlobal.password.hash(password);

  // Create the user
  const user = await MyGlobal.prisma.minimal_todo_taskusers.create({
    data: {
      id,
      email,
      password_hash: hashedPassword,
      created_at: now,
      updated_at: now,
    },
  });

  // Calculate token expiration times
  const accessExpiresInMs = 60 * 60 * 1000; // 1 hour
  const refreshExpiresInMs = 7 * 24 * 60 * 60 * 1000; // 7 days

  const accessExpiredAt = toISOStringSafe(
    new Date(Date.now() + accessExpiresInMs),
  );
  const refreshableUntil = toISOStringSafe(
    new Date(Date.now() + refreshExpiresInMs),
  );

  // Generate tokens
  const accessToken = jwt.sign(
    {
      userId: user.id,
      email: user.email,
      type: "taskUser",
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "1h",
      issuer: "autobe",
    },
  );

  const refreshToken = jwt.sign(
    {
      userId: user.id,
      tokenType: "refresh",
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "7d",
      issuer: "autobe",
    },
  );

  // Return the authorized response
  return {
    user: {
      id: user.id,
      email: user.email,
      created_at: toISOStringSafe(user.created_at),
      updated_at: toISOStringSafe(user.updated_at),
      deleted_at: user.deleted_at ? toISOStringSafe(user.deleted_at) : null,
    },
    token: {
      access: accessToken,
      refresh: refreshToken,
      expired_at: accessExpiredAt,
      refreshable_until: refreshableUntil,
    },
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { IMinimalTodoTaskUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IMinimalTodoTaskUser";
import { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import { TaskuserPayload } from "../decorators/payload/TaskuserPayload";

/**
 * Authenticates users with email and password for the minimalTodo application.
 *
 * This endpoint authenticates existing users by verifying their email and
 * password credentials against stored records in the system.
 *
 * The operation processes user-submitted credentials by first locating the
 * matching record in the `minimal_todo_taskusers` table using the provided
 * email. It then compares the submitted password with the stored
 * `password_hash` using secure cryptographic verification. During
 * authentication, the system validates that the account is active (where
 * `deleted_at` is null) before granting access, enforcing account status
 * management according to business rules.
 *
 * Upon successful verification, the system creates a new session record in the
 * `minimal_todo_sessions` table, generating a cryptographically secure token
 * with appropriate expiration. The session includes the user's IP address for
 * security monitoring and establishes a 30-day validity period before requiring
 * re-authentication, balancing security with user convenience as specified in
 * business requirements.
 *
 * Security measures include protection against timing attacks through
 * consistent response times, rate limiting after multiple failed attempts, and
 * generic error messages that don't reveal whether the email or password was
 * incorrect. All password operations occur in memory without logging to
 * maintain security standards. Session tokens are transmitted securely and
 * stored with appropriate HTTP security flags.
 *
 * Related operations include the join operation for new users, the refresh
 * operation for maintaining active sessions, and the password reset flows for
 * account recovery. This login operation forms the central authentication point
 * for all returning users, enabling secure access to personal task management
 * functionality while maintaining the minimal security profile appropriate for
 * the application's scope.
 *
 * @param props - Request properties
 * @param props.body - Email and password credentials for user authentication
 * @returns Authentication tokens for accessing the application
 * @throws {Error} When credentials are invalid or user account is deactivated
 */
export async function post__auth_taskUser_login(props: {
  body: IMinimalTodoTaskUser.ILogin;
}): Promise<IMinimalTodoTaskUser.IAuthorized> {
  const { body } = props;

  // Find user by email, ensuring account is active (not deleted)
  const user = await MyGlobal.prisma.minimal_todo_taskusers.findFirst({
    where: {
      email: body.email,
      deleted_at: null,
    },
  });

  // Check if user exists and password is valid
  if (
    !user ||
    !(await MyGlobal.password.verify(body.password, user.password_hash))
  ) {
    throw new Error("Invalid credentials");
  }

  // Generate session token
  const sessionToken = v4();

  // Set session expiration (30 days)
  const expiresAt = new Date();
  expiresAt.setDate(expiresAt.getDate() + 30);

  // Extract IP address from request - in practice, this would come from the actual request object
  const ipAddress = "127.0.0.1";

  // Create session with all required fields including ip_address
  await MyGlobal.prisma.minimal_todo_sessions.create({
    data: {
      id: v4(),
      minimal_todo_taskuser_id: user.id,
      token: sessionToken,
      expires_at: toISOStringSafe(expiresAt),
      created_at: toISOStringSafe(new Date()),
      ip_address: ipAddress,
    },
  });

  // Generate access token with proper payload structure
  const accessToken = jwt.sign(
    {
      userId: user.id,
      email: user.email,
      type: "taskUser",
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "1h",
      issuer: "autobe",
    },
  );

  // Generate refresh token
  const refreshToken = jwt.sign(
    {
      userId: user.id,
      tokenType: "refresh",
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "7d",
      issuer: "autobe",
    },
  );

  // Set access token expiration (1 hour from now)
  const accessTokenExpires = new Date();
  accessTokenExpires.setHours(accessTokenExpires.getHours() + 1);

  return {
    user: {
      id: user.id,
      email: user.email,
      created_at: toISOStringSafe(user.created_at),
      updated_at: toISOStringSafe(user.updated_at),
      deleted_at: user.deleted_at ? toISOStringSafe(user.deleted_at) : null,
    },
    token: {
      access: accessToken,
      refresh: refreshToken,
      expired_at: toISOStringSafe(accessTokenExpires),
      refreshable_until: toISOStringSafe(expiresAt),
    },
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { IMinimalTodoTaskUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IMinimalTodoTaskUser";
import { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import { TaskuserPayload } from "../decorators/payload/TaskuserPayload";

/**
 * Refreshes authentication tokens using a valid refresh token.
 *
 * This endpoint validates refresh tokens and issues new access tokens to
 * maintain user sessions without requiring re-authentication with primary
 * credentials.
 *
 * The operation processes refresh tokens by locating the corresponding session
 * record in the `minimal_todo_sessions` table, verifying that the token exists
 * and hasn't expired (checking against the `expires_at` timestamp). During
 * token validation, the system compares the current Asia/Seoul time against the
 * stored expiration to determine token validity, ensuring session security
 * while providing convenience for active users.
 *
 * Upon successful validation, the system generates new authentication tokens
 * and updates the session's expiration time while preserving the associated
 * user identity. The session's `created_at` timestamp remains unchanged as it
 * represents the original authentication event, while security monitoring
 * tracks refresh activity through the session records.
 *
 * Security considerations include single-use tokens for enhanced security,
 * binding tokens to the originating IP address for fraud detection, and strict
 * expiration policies that limit token lifespan. The refresh mechanism operates
 * independently from primary authentication to minimize exposure of user
 * credentials while maintaining session continuity.
 *
 * Related operations include the login operation that creates the initial
 * session, and the password reset flows that invalidate existing sessions. This
 * refresh operation supports the business requirement for maintaining active
 * user sessions for 30 days of inactivity, enhancing user experience while
 * adhering to appropriate security standards for the minimalTodo application's
 * scope.
 *
 * @param props - Request parameters
 * @param props.body - Refresh token to validate and exchange for new access
 *   tokens
 * @returns New authentication tokens with extended validity
 * @throws {Error} When the refresh token is invalid or expired
 * @throws {Error} When the user account has been deleted
 */
export async function post__auth_taskUser_refresh(props: {
  body: IMinimalTodoTaskUser.IRefresh;
}): Promise<IMinimalTodoTaskUser.IAuthorized> {
  const { refreshToken } = props.body;

  // Find session with the refresh token
  const session = await MyGlobal.prisma.minimal_todo_sessions.findUnique({
    where: { token: refreshToken },
    include: { user: true },
  });

  if (!session) {
    throw new Error("Invalid refresh token");
  }

  // Check if session has expired
  // Convert both dates to ISO strings for proper comparison
  const currentSeoulTime = toISOStringSafe(new Date());
  const sessionExpiresAt = toISOStringSafe(session.expires_at);

  if (sessionExpiresAt < currentSeoulTime) {
    throw new Error("Expired refresh token");
  }

  // Check if user account is deleted
  if (session.user.deleted_at) {
    throw new Error("User account has been deleted");
  }

  // Generate new tokens with proper expiration
  // Access token expires in 1 hour (3600000 ms)
  // Refresh token expires in 30 days (2592000000 ms)
  const oneHourFromNow = toISOStringSafe(new Date(Date.now() + 3600000));
  const thirtyDaysFromNow = toISOStringSafe(new Date(Date.now() + 2592000000));

  // Update session expiration time
  await MyGlobal.prisma.minimal_todo_sessions.update({
    where: { id: session.id },
    data: { expires_at: thirtyDaysFromNow },
  });

  // Create response with proper ISO string formatting for all dates
  return {
    user: {
      id: session.user.id,
      email: session.user.email,
      created_at: toISOStringSafe(session.user.created_at),
      updated_at: toISOStringSafe(session.user.updated_at),
      deleted_at: session.user.deleted_at
        ? toISOStringSafe(session.user.deleted_at)
        : null,
    },
    token: {
      access: v4(),
      refresh: v4(),
      expired_at: oneHourFromNow,
      refreshable_until: thirtyDaysFromNow,
    },
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
